	private String json;
	protected static final int MSG_LOGIN_RESULT = 0;
	private ArrayList<CityEntity> cities = new ArrayList<CityEntity>();
	public String getJson() {
		return json;
	}

	public void setJson(String json) {
		this.json = json;
	}

	public ArrayList<CityEntity> Getcity() {
		try {
			JSONObject jsonObject = new JSONObject(json);
			JSONObject result = jsonObject.getJSONObject("result");
			Iterator<String> it = result.keys();
			while (it.hasNext()) {
				String key = it.next();
				JSONObject city = result.getJSONObject(key);
				JSONArray citys = city.getJSONArray("citys");
				for (int i = 0; i < citys.length(); i++) {
					CityEntity cityEntity = new CityEntity();
					cityEntity.setCityCode(citys.getJSONObject(i).getString("city_code"));
					cityEntity.setCityName(citys.getJSONObject(i).getString("city_name"));
					cities.add(cityEntity);
				}
			}
		} catch (JSONException e) {
			e.printStackTrace();
		}
		return cities;
	}
	
	
	
	
	
	
	
